import { useState, useRef } from "react";

const Editor = () => {

  const [textarea, setTextarea] = useState('');
  const newText = useRef('')
  
  return (
    <div className="panel editor">
      <header className="h2">
        <i className="fa fa-free-code-camp"></i> Editor
      </header>
      <textarea
        className="form-control"
        autoFocus
        value={textarea}
        text = {textarea}
        onChange={(e) => setTextarea(e.target.value)}
      ></textarea>
    </div>
  );
};

const Previewer = ({ text }) => {
  return (
    <div className="panel previewer">
      <header className="h2">
        <i className="fa fa-free-code-camp"></i> Previewer
      </header>
      <textarea className="form-control" readOnly></textarea>
    </div>
  );
};

const Container = () => {
  return (
    <div className="contenedor d-flex justify-content-center align-items-stretch gap-2 text-center flex-wrap p-3">
      <Editor />
      <Previewer />
    </div>
  );
};

const App = () => {
  return (
    <>
      <Container />
    </>
  );
};

export default App;
