const Editor = () => {
  return (
    <div className="panel editor">
      <header className="h2">Editor</header>
      <textarea className="form-control" ></textarea>
    </div>
  );
};

const Previewer = () => {
  return (
    <div className="panel previewer">
      <header className="h2">Previewer</header>
      <textarea className="form-control" ></textarea>
    </div>
  );
};

const Container = () => {
  return (
    <div className="d-flex justify-content-center align-items-stretch gap-2 text-center flex-wrap h-100vh">
      <Editor />
      <Previewer />
    </div>
  );
};

const App = () => {
  return (
    <>
      <Container />
    </>
  );
};

export default App;
