import { useRef } from "react";
import { marked } from "marked";
import Prism from "prismjs";

marked.use({
  breaks: true,
  mangle: false,
  headerIds: false
})

marked.setOptions({
  highlight: function (e) {
    return Prism.highlight(e, Prism.languages.javascript, "javascript")
  }
}
  
)
const App = () => {

  const inputText = useRef("");

  const handleInputChange = () => {
    const rawMarkup = marked.parse(inputText.current.value)
    document.getElementById("preview").innerHTML = rawMarkup;
  };

  return (
    <div className="contenedor d-flex flex-row justify-content-center align-items-stretch gap-4 flex-wrap p-3">
      <div className="panel editor">
        <header className="h2">
          <i className="fa fa-free-code-camp"></i> Editor
        </header>
        <textarea
          id="editor"
          className="form-control"
          ref={inputText}
          autoFocus
          onKeyUpCapture={handleInputChange}
        ></textarea>
      </div>
      <div className="panel previewer">
        <header className="h2">
          <i className="fa fa-free-code-camp"></i> Previewer
        </header>
        <div id="preview" className="form-control"></div>
      </div>
    </div>
  );
};

export default App;
