const Editor = () => {
  return (
    <div className="panel editor">
      <header className="h2"><i className="fa fa-free-code-camp"></i> Editor</header>
      <textarea className="form-control" autoFocus></textarea>
    </div>
  );
};

const Previewer = () => {
  return (
    <div className="panel previewer">
      <header className="h2"><i className="fa fa-free-code-camp"></i> Previewer</header>
      <textarea className="form-control" readOnly></textarea>
    </div>
  );
};

const Container = () => {
  return (
    <div className="contenedor d-flex justify-content-center align-items-stretch gap-2 text-center flex-wrap p-3">
      <Editor />
      <Previewer />
    </div>
  );
};

const App = () => {
  return (
    <>
      <Container />
    </>
  );
};

export default App;
