import { useState, useRef } from "react";

const Editor = () => {

  const [textEditor, setTextEditor] = useState('');
  const textPreviewer = useRef(null)
  
  return (
    <div>
      <div className="panel editor">
      <header className="h2">
        <i className="fa fa-free-code-camp"></i> Editor
      </header>
      <textarea
        className="form-control"
        autoFocus
        value={textEditor}
        ref={textPreviewer}
        onChange={() => setTextEditor(textPreviewer.current.value)}
      ></textarea>
    </div>
    <div className="panel previewer">
      <header className="h2">
        <i className="fa fa-free-code-camp"></i> Previewer
      </header>
      <textarea className="form-control" readOnly>{textEditor}</textarea>
    </div>
    </div>
  );
};


const Container = () => {
  return (
    <div className="contenedor d-flex justify-content-center align-items-stretch gap-2 text-center flex-wrap p-3">
      <Editor />
    </div>
  );
};

const App = () => {
  return (
    <>
      <Container />
    </>
  );
};

export default App;
