import { useState} from "react";
import { marked } from "marked";

const Editor = () => {

  const [inputText, setInputText] = useState('');

  const handleChange = (e) => {
    console.log(e.target.value)
    setInputText(e.target.value)
    const rawMarkup = marked(inputText, { sanitize: true });
    document.getElementById('preview').innerHTML = rawMarkup;
  }

  return (
    <div>
      <div className="panel editor">
      <header className="h2">
        <i className="fa fa-free-code-camp"></i> Editor
      </header>
      <textarea
        id="editor"
        className="form-control"
        autoFocus
        value={inputText}
        onKeyUpCapture={() => handleChange}
      ></textarea>
    </div>
    <div className="panel previewer">
      <header className="h2">
        <i className="fa fa-free-code-camp"></i> Previewer
      </header>
      <div id="preview" className="form-control" readOnly></div>
    </div>
    </div>
  );
};


const Container = () => {
  return (
    <div className="contenedor d-flex justify-content-center align-items-stretch gap-2 text-center flex-wrap p-3">
      <Editor />
    </div>
  );
};

const App = () => {
  return (
    <>
      <Container />
    </>
  );
};

export default App;
