import { useState } from "react";
import { marked } from "marked";

marked.use({
  breaks: true
})
const App = () => {

  const [inputText, setInputText] = useState("");

  const handleInputChange = (event) => {
    console.log(event.target.value)
    setInputText(event.target.value);
    const rawMarkup = marked.parse(inputText.replace(/^[\u200B\u200C\u200D\u200E\u200F\uFEFF]/,""))
    document.getElementById("preview").innerHTML = rawMarkup;
  };

  return (
    <div className="contenedor d-flex flex-row justify-content-center align-items-stretch gap-4 flex-wrap p-3">
      <div className="panel editor">
        <header className="h2">
          <i className="fa fa-free-code-camp"></i> Editor
        </header>
        <textarea
          id="editor"
          className="form-control"
          autoFocus
          onChange={handleInputChange}
        ></textarea>
      </div>
      <div className="panel previewer">
        <header className="h2">
          <i className="fa fa-free-code-camp"></i> Previewer
        </header>
        <div id="preview" className="form-control" readOnly></div>
      </div>
    </div>
  );
};

export default App;
