import { marked } from "marked";

const App = () => {
  return (
    <div className="contenedor d-flex flex-row justify-content-center align-items-stretch gap-4 flex-wrap p-3">
      <div className="panel editor">
        <header className="h2">
          <i className="fa fa-free-code-camp"></i>
          <span>Editor</span>
        </header>
        <textarea
          id="editor"
          className="form-control"
          autoFocus
          onKeyUp={(e) => {
            const currentContent = e.target.value;
            document.getElementById("preview").innerHTML = marked(
              currentContent,
              { sanitize: true }
            );
          }}
        ></textarea>
      </div>
      <div className="panel previewer">
        <header className="h2">
          <i className="fa fa-free-code-camp"></i> Previewer
        </header>
        <div id="preview" className="form-control" readOnly></div>
      </div>
    </div>
  );
};

export default App;
